def rob(nums, colors) -> int:
    n = len(colors)
    robs = []
    for i in range(0, n-1):
        if colors[i] == colors[i + 1]:
            robs.append(max(nums[i], nums[i + 1]))
        else:
            robs.append(nums[i])

    robsb = []
    for i in range(n-1, 0 , -1):
        if colors[i] == colors[i - 1]:
            robsb.append(max(nums[i], nums[i - 1]))
        else:
            robsb.append(nums[i])

    return max(sum(robs), sum(robsb))

